var plusButtonFemale = document.querySelector(".btn_plus_female");

if (minusButtonFemale) {
	if (plusButtonFemale) {
	minusButtonFemale.addEventListener("click", function() {
		minusButtonFemale.style.display = "none";
		plusButtonFemale.style.display = "inline";
	});

	plusButtonFemale.addEventListener("click", function() {
		plusButtonFemale.style.display = "none";
		minusButtonFemale.style.display = "inline";
	});
	}
}

