function revertProcessInputPluses(input) {
	var output = input.replace(/\+\((\d+)\)/g, function(match, count) {
			return '+,'.repeat(parseInt(count)) + ',';
		});
	output = output.replace(/,+/g, ',');
	return output;
}

function showBeginScan(type, element_id) {
var scan_type = type;
scan_input = document.getElementById('type_of_scan');
scan_input.value = scan_type;
var popup = document.getElementById('tvs_scan_popup');
popup.style.display = 'block';

inputValue = document.getElementById(element_id).value;
inputValue = revertProcessInputPluses(inputValue);
document.querySelector('.output_results').innerText = inputValue;
}

