function cancelPopup() {
var popup = document.getElementById('tvs_scan_popup');
popup.style.display = 'none';
}

function processDisplay(dataListText) {
let dataList = dataListText.split(',');
let plusElements = dataList.filter(element => element === '+');
let greaterThan22Elements = dataList.filter(element => element === '>22');
dataList = dataList.filter(element => {
if (element !== '+' ) {
if (element !== '>22') {
return true;
}
}
return false;
});

let sortedData = dataList.sort((a, b) => {
if (!isNaN(parseFloat(a))) {
if(!isNaN(parseFloat(b))){
return parseFloat(a) - parseFloat(b);
}
} else {
return a.localeCompare(b);
}
});
let resultList = plusElements.concat(sortedData, greaterThan22Elements);
let resultStr = resultList.filter(Boolean).join(',');
return resultStr;
}
function processInputPluses(processed_input) {
	var plusCount = 0;
	var output = processed_input.replace(/\+/g, function() {
		plusCount++;
		return '';
	});
	output = output.replace(/,+/, ',');
	if (plusCount > 0) {
		output = '+(' + plusCount + ')' + output;
	}
	return output;
}

function saveTvsScan() {
scan_input = document.getElementById('type_of_scan').value;
var popup = document.getElementById('tvs_scan_popup');
popup.style.display = 'none';
var inputValue = document.querySelector('.output_results').innerText;
processed_value_initial = processDisplay(inputValue)+',';
processed_value = processInputPluses(processed_value_initial);
processed_value = processed_value === ',' ? null : processed_value;

if (scan_input == "lov")
{
var inputField = document.getElementById('tvs_lov');
inputField.value = processed_value;
inputField.dispatchEvent(new Event('input', { bubbles: true }));
}
else if (scan_input == "rov")
{
var inputField = document.getElementById('tvs_rov');
inputField.value = processed_value;
inputField.dispatchEvent(new Event('input', { bubbles: true }));
}
else if (scan_input == "gynae_rov")
{
var inputField = document.getElementById('gynae_rov');
inputField.value = processed_value;
inputField.dispatchEvent(new Event('input', { bubbles: true }));
}
else if (scan_input == "gynae_lov")
{
var inputField = document.getElementById('gynae_lov');
inputField.value = processed_value;
inputField.dispatchEvent(new Event('input', { bubbles: true }));
}
else
{
return;
}
}

section.script:
document.getElementById("collapseButton").addEventListener("click", function() {
var repeatKanbanDivs = document.querySelectorAll(".repeat_kanban");

repeatKanbanDivs.forEach(function(repeatKanbanDiv) {
var collapseDivs = repeatKanbanDiv.querySelectorAll(".collapse");

collapseDivs.forEach(function(collapseDiv) {
var elements = document.querySelectorAll('[data-bs-target*="family_history_section"]');

elements.forEach(function(element) {
if (element.querySelector('.fa-angle-up')) {
element.querySelector('.fa-angle-up').classList.remove('fa-angle-up');
element.querySelector('.fa').classList.add('fa-angle-down');
}
});

collapseDiv.classList.remove("show");

});
});
});


document.getElementById("expandButton").addEventListener("click", function() {
var repeatKanbanDivs = document.querySelectorAll(".repeat_kanban");

repeatKanbanDivs.forEach(function(repeatKanbanDiv) {
var collapseDivs = repeatKanbanDiv.querySelectorAll(".collapse");

collapseDivs.forEach(function(collapseDiv) {
var elements = document.querySelectorAll('[data-bs-target*="family_history_section"]');

elements.forEach(function(element) {
if (element.querySelector('.fa-angle-down')) {
element.querySelector('.fa-angle-down').classList.remove('fa-angle-down');
element.querySelector('.fa').classList.add('fa-angle-up');
}
});
collapseDiv.classList.add("show");
});
});
});

